INSERT INTO verses_view(book_id, chapter, verse_number, verse)
VALUES
(41, 1, 0, 'Прорицање казне злурадим Едомцима и избављења Израиљу.'),
(41, 1, 1, 'Утвара Авдијева. Овако вели Господ Господ за Едомску: Чусмо глас од Господа, и гласник би послан к народима: Устајте, да устанемо на њу у бој.'),
(41, 1, 2, 'Гле, учинићу те малијем међу народима, бићеш врло презрен.'),
(41, 1, 3, 'Понос срца твојега превари те, тебе који живиш у расјелинама каменијем, у високом стану свом, и говориш у срцу свом: Ко ће ме оборити на земљу?'),
(41, 1, 4, 'Да подигнеш високо као орао и међу звијезде да метнеш гнијездо своје, оданде ћу те оборити, говори Господ.'),
(41, 1, 5, 'Како си оплијењен? Да су дошли к теби крадљивци или лупежи ноћу, не би ли покрали колико им је доста? Да су дошли к теби берачи виноградски, не би ли оставили пабирака?'),
(41, 1, 6, 'Како се претражи Исав, како се нађоше потаје његове!'),
(41, 1, 7, 'До границе те одведоше сви који бијаху с тобом у вјери, преварише те и надвладаше те који бијаху у миру с тобом; који једу хљеб твој подметнуше ти замку да се не опази.'),
(41, 1, 8, 'У онај дан, говори Господ, нећу ли погубити мудре у земљи Едомској и разумне у гори Исавовој?'),
(41, 1, 9, 'И твоји ће се јунаци уплашити, Темане, да се истријебе покољем сви из горе Исавове.'),
(41, 1, 10, 'За насиље учињено брату твојему Јакову покриће те стид и истријебићеш се за свагда.'),
(41, 1, 11, 'Онај дан кад ти стајаше насупрот, онај дан кад иностранци одвођаху у ропство војску његову, и туђинци улажаху на врата његова и бацаху ждријеб за Јерусалим, бјеше и ти као који од њих.'),
(41, 1, 12, 'Али ти не требаше гледати дана брата својега, дана кад се одвођаше у туђу земљу, нити се радовати синовима Јудинијем у дан кад пропадаху, нити разваљивати уста у дан невоље њихове.'),
(41, 1, 13, 'Не требаше ти ући на врата народа мојега у дан погибли њихове, не требаше да и ти гледаш зло њихово у дан погибли њихове, ни да се машаш добра њихова у дан погибли њихове.'),
(41, 1, 14, 'Нити требаше да станеш на распутицу да убијаш бјежан њихову, нити да издајеш онијех који осташе у дан невоље.'),
(41, 1, 15, 'Јер је Дан Господњи близу свијем народима; како си чинио, тако ће ти бити, плата ће ти се вратити на главу твоју.'),
(41, 1, 16, 'Јер као што сте ви пили на Светој гори мојој, тако ће пити сви народи вазда, пиће и ждријеће, и биће као да их није било.'),
(41, 1, 17, 'А на гори ће Сиону бити спасење, и биће света, и дом ће Јаковљев наслиједити нашљедство своје.'),
(41, 1, 18, 'И дом ће Јаковљев бити огањ и дом Јосифов пламен, а дом Исавов стрњика; и разгорјеће се на њих, и спалиће их; и неће бити остатка дому Исавову, јер Господ рече.'),
(41, 1, 19, 'И наслиједиће југ, гору Исавову, и равницу, Филистеје; и наслиједиће поље Јефремово и поље Самаријско и Венијаминово и Галад;'),
(41, 1, 20, 'И заробљена војска синова Израиљевијех наслиједиће што је било Хананејско до Сарепте; а робље Јерусалимско, што је у Сефараду, наслиједиће јужне градове.'),
(41, 1, 21, 'И Избавитељи ће изаћи на гору Сион да суде гори Исавовој, и Царство ће бити Господње.');
